def legendre_symbol(a, p):
    ls = pow(a, (p - 1) // 2, p)
    return -1 if ls == p - 1 else ls

def tonelli_shanks(n, p):
    Q = p - 1
    S = 0
    while Q % 2 == 0:
        Q //= 2
        S += 1
    if S == 1:
        r = pow(n, (p + 1) // 4, p)
        return r, p - r
    for z in range(2, p):
        if p - 1 == legendre_symbol(z, p):
            break
    c = pow(z, Q, p)
    r = pow(n, (Q + 1) // 2, p)
    t = pow(n, Q, p)
    M = S
    while t != 1:
        for i in range(1, M):
            if pow(t, 2 ** i, p) == 1:
                break
        b = pow(c, 2 ** (M - i - 1), p)
        r = (r * b) % p
        t = (t * b * b) % p
        c = (b * b) % p
        M = i
    return r, p - r

